import React, { useState } from "react";
import axios from "axios";
import './weather.css';


export default function Weather(props) {
    const [ready, setready] = useState(false);
    const [tempearutre, settemperature] = useState(null);
    function handdleresp(response) {
        alert(`the weather in ${response.data.name} is ${response.data.main.temp}`);
        settemperature(response.data.main.temp);
    }

    if (ready) {
        return (
            <div className="Weather">
                <form>
                    <div className="row">
                        <div className="col-9">
                            <input type="search" placeholder="enter a city..." className="form-control"></input>

                        </div>
                        <div className="col-3">
                            <input type="submit" value="search" className="btn btn-primary"></input>

                        </div>
                    </div>
                </form>
                <h2>new york</h2>
                <ul>
                    <li>wensday 07:08</li>
                    <li>mostly cloudy</li>
                </ul>
                <p>{tempearutre}</p>
                <div className="row">
                    <div className="col-6">
                        <img src="https://ssl.gstatic.com/onebox/weather/64/partly_cloudy.png"></img>
                    </div>
                </div>
            </div>
        );
    }
    else {
        let city = "london";
        let api = `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=72bb9dab46b9ec3d65f423c63f27a9b8`;

        axios.get(api).then(handdleresp);
        return "loading...";
    }
}
